mapper.update:
update resume set 
rstitle = #{rstitle}, rscareer = #{rscareer},rscompany = #{rscompany},rsworkstart = #{rsworkstart},rsworkend = #{rsworkend}, rswork= #{rswork}
, rsacademic= #{rsacademic}, rsacademicstat= #{rsacademicstat}, rsintroduce= #{rsintroduce},rslicense = #{rslicense}
, rslicenseorg = #{rslicenseorg}, rslicenseyear= #{rslicenseyear}, rslanguage= #{rslanguage}, rslanguagelevel= #{rslanguagelevel}
, rsmoddate= sysdate() , rsphoto = #{rsphoto}
where rsid = #{rsid} and sid = #{sid}
